import {createContext, useReducer, useContext} from "react";

const ToDoContext = createContext(); //declare a vaiable to create context

const initialState = { //Big Picture
    todos:[]
}

const messageReducer = (state, action) => {
    switch (action.type) {
        case "ADD_TODO":
            return {
                todos: [...state.todos,action.payload], //everything that was there ( ...state.todo) and then some(action.payload)
            };
        case "REMOVE_TODO":
            return {
                todos:state.todos.filter((todo) => todo.id !== action.payload.id), //filter out payload.id
            };
        default:
            return state;
    }
};

export const ToDoProvider = ({children}) => {
    const [state, dispatch] = useReducer(messageReducer, initialState);
    console.log(state);
    return (
        <ToDoContext.Provider value = {{state, dispatch}}>
            {children}
        </ToDoContext.Provider>
    )
};

export const useToDo = () => useContext(ToDoContext)
